import "./App.css";
import { Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import HomePage from "./pages/HomePage";
import SignupPage from "./pages/SignupPage"; // <== IMPORT
import LoginPage from "./pages/LoginPage"; // <== IMPORT
import IsPrivate from "./components/IsPrivate"; // <== IMPORT
import IsAnon from "./components/IsAnon"; // <== IMPORT
import ItemDetailsPage from "./pages/ItemDetailsPage";
import Cart from "./pages/Cart";
import Java from "./Categories/Java";
import Javascript from "./Categories/Javascript";
import Python from "./Categories/Python";
import Shop from "./pages/Shop";
import Sell from "./components/Sell";
import Inventory from "./components/Inventory";
import Ruby from "./Categories/Ruby";
import { useState, useEffect } from "react";
import { AuthContext } from "../src/context/auth.context";
import { useContext } from "react";
import axios from "axios";
import { useParams, Link, Navigate } from "react-router-dom";

function App() {
  const { user } = useContext(AuthContext);
  console.log(user?._id);
  const key =
    "pk_test_51M9ZjvG6NeaDtKpVRFtUgmoFVxEMaTjHtPct35DcaB1DLIyvoEqXQ6vAvcgqKcp7cfjeIs5J0ZH94EhjCsSyWN7Z00xeCRkTs7";

  const [cart, setCart] = useState(null);
  const API_URL = "https://codebooks.fly.dev";

  const getAllItems = () => {
    const storedToken = localStorage.getItem("authToken");
    axios
      .get(`${API_URL}/auth/user/${user}`, {
        headers: { Authorization: `Bearer ${storedToken}` },
      })
      .then((response) => {
        setCart(response.data.cart);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getAllItems();
  }, []);

  return (
    <div className="App">
      <Navbar cartAmount={cart} />
      <Routes>
        <Route path="/" element={<HomePage />} />

        <Route
          path="/signup"
          element={
            <IsAnon>
              <SignupPage />{" "}
            </IsAnon>
          }
        />
        <Route
          path="/login"
          element={
            <IsAnon>
              <LoginPage />
            </IsAnon>
          }
        />
        <Route path="/item/:itemId" element={<ItemDetailsPage />} />
        <Route path="/user/:userId" element={<Cart cart={cart} />} />
        <Route path="/javascript" element={<Javascript />} />
        <Route path="/python" element={<Python />} />
        <Route path="/java" element={<Java />} />
        <Route path="/ruby" element={<Ruby />} />
        <Route path="/shop" element={<Shop />} />
        <Route path="/sell/:userId" element={<Sell />} />
        <Route path="/inventory/:userId" element={<Inventory />} />
      </Routes>
    </div>
  );
}

export default App;
